mapper.delete:

delete from sys_data_struct_node_attribute
where struct_code = #{struct_code,jdbcType=VARCHAR}
and node_code = #{node_code,jdbcType=VARCHAR}
and attribute_code = #{attribute_code,jdbcType=VARCHAR}

mapper.update:

update sys_data_struct_node_attribute
set attribute_value = #{attribute_value,jdbcType=VARCHAR}
where struct_code = #{struct_code,jdbcType=VARCHAR}
and node_code = #{node_code,jdbcType=VARCHAR}
and attribute_code = #{attribute_code,jdbcType=VARCHAR}

mapper.select:

select struct_code, node_code, attribute_code, attribute_value
from sys_data_struct_node_attribute
where struct_code = #{struct_code,jdbcType=VARCHAR}
and node_code = #{node_code,jdbcType=VARCHAR}
and attribute_code = #{attribute_code,jdbcType=VARCHAR}
