mapper.delete:

delete from sys_data_struct_node_attribute
where struct_code = #{structCode,jdbcType=VARCHAR}
and node_code =#{nodeCode,jdbcType=VARCHAR}
and attribute_code = #{attributeCode,jdbcType=VARCHAR}

mapper.update:

update sys_data_struct_node_attribute
set attribute_value =#{attributeValue,jdbcType=VARCHAR}
where struct_code = #{structCode,jdbcType=VARCHAR}
and node_code =#{nodeCode,jdbcType=VARCHAR}
and attribute_code = #{attributeCode,jdbcType=VARCHAR}

mapper.select:

select struct_code, node_code, attribute_code, attribute_value
from sys_data_struct_node_attribute
where struct_code = #{structCode,jdbcType=VARCHAR}
and node_code =#{nodeCode,jdbcType=VARCHAR}
and attribute_code = #{attributeCode,jdbcType=VARCHAR}
