mapper.select:
select goods_id ,delete_flag ,create_date ,goods_description ,goods_name ,is_hot ,is_new_product ,up_off_shelf ,goods_name_en ,ddi.item_name styleType ,dsn2.node_name subStyle ,goods_sell_price ,designer_id ,is_suit ,custom_flag ,app_index_show ,app_index_order ,details_intro_url ,need_detail_generate ,delete_date ,dsn1.node_name main_style ,fabric_name from app_goods ag LEFT JOIN sys_data_struct_node dsn1 on ag.main_style=dsn1.node_code and dsn1.struct_code='app_goods' LEFT JOIN sys_data_struct_node dsn2 on ag.sub_style=dsn2.node_code and dsn2.struct_code='app_goods' LEFT JOIN sys_data_dict_item ddi on ag.style_type=ddi.item_code and ddi.type_code='app_goos_style' where 1=1 and delete_flag='N'
${_parameter}
order by create_date desc

mapper.update:
update app_Goods
   set up_off_shelf = #{upOffShelf}
 where 1 = 1
   and goods_id = #{goodsId}
